import React from 'react';
import { Jumbotron as Jumbo, Container } from 'react-bootstrap';
import styled from 'styled-components';
import boatImage from '../images/IMG_1693.JPG';
import Footer from '../components/Footer';

const Styles = styled.div`
  .jumbo {
    background: url(${boatImage}) no-repeat fixed bottom;
    background-size: cover;
    color: #efefef;
    height: 850px;
    position: relative;
    z-index: -4;
  }
  .overlay {
    background-color: #000;
    opacity: 0.6;
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    z-index: -3;
  }
  .welcome {
    text-align: center;
    margin-top: 80px;
    font-size: 50px;
  }
  .snips {
    font-size: 20px;
  }
`;

export const Motd = () => (
  <Styles>
    <Jumbo fluid className="jumbo">
      <div className="overlay"></div>
      <Container className="welcome">
        <h1>Welcome</h1>
        <p>Student during the day, Engineer and photogropher at night!</p>
        <p className = "snips"> Full-Stack Web Developer | IT Specalist and Technincain | Network and System Administration | IT Consultion </p>
        <a href = "#aboutme" id="about">Click here for learn more! </a>
      </Container>
    </Jumbo>
    <Footer />
  </Styles>
)
